#!/usr/bin/env python3

from gmpy2 import isqrt, isqrt_rem, mpz, invert, powmod

N = mpz()
ct = mpz()

s, t = isqrt_rem(N)
A = s + (1 if t > 0 else 0)
x = isqrt(A ** 2 - N)
p = A - x
q = A + x
phi_N = (p - 1) * (q - 1)
d = invert(65537, phi_N)  # divm(phi_N + 1, 65537, phi_N)
pt = hex(powmod(ct, d, N))
print(bytes.fromhex(pt[pt.find('00') + 2:]))
